function addAnnouncement(n){var e=$("<article>").trunk8({lines:16,tooltip:!1});$("#announcements").append($("<div>").addClass("col-lg-6").append($("<h3>").addClass("title").text(n.title)).append(e).append($("<h4>").addClass("continue-reading").append($("<a>").attr("ref","_blank").attr("href",n.url).text("Continue Reading")))),e.trunk8("update",n.content)}$.getJSON("/announcements.json",function(n){$("#announcements").empty(),addAnnouncement(n.first),addAnnouncement(n.second)});